import React from 'react'
import '../App.css';

function TheGang(props) {
    return (
        <div className="card" style={cardStyle} onClick={() => props.clickGangMember(props.id)} className="click">
            <strong>Name:</strong> {props.name}
            <img style={imgStyle} alt={props.name} src={props.image} />
        </div>
    );
}

const imgStyle = {
    height: "215px",
    width: "250px"
}

const cardStyle = {
    margin: "40px 40px",
    display: "inline-block"

}

export default TheGang;
